/**
 * User.js
 *
 * @description :: The User object.
 * @docs        :: http://sailsjs.org/documentation/concepts/models-and-orm/models
 */
var bcrypt = require('bcrypt');
module.exports = {

    schema: true,
    autoPK: true,

    attributes: {

        // not shown: id, autogenerated

        // main username for chat
        username: {
            type: 'string',
            required: true,
            index: true
        },

        // random 4 digit id, enforced per username
        discriminator: {
            type: 'integer',
            defaultsTo: () => Math.floor(1000 + Math.random() * 9000),
            required: true
        },

        // profile picture
        avatar: {
            type: 'string', // Base64 hash
            defaultsTo: 'data:image/png;base64,',
            required: true
        },

        // email is used to log in after the trial period
        email: {
            type: 'string', // default null for trial
            defaultsTo: 'trial',
            index: true
        },

        // used with email to log in
        password: {
            type: 'string', // hashed, default null for trial
            defaultsTo: 'trial'
        },

        // true if user has verified their email
        emailConfirmed: {
            type: 'boolean',
            defaultsTo: false
        },

        toJSON: function () {
            var values = this.toObject();
            delete values.password;
            return values;
        },

        // user can set their current status, e.g. 'watching movies'
        status: {
            type: 'string',
            defaultsTo: 'another noob',
            required: true
        },

        // contains all the channels a user is currently part of
        channels: {
            type: 'array',
            defaultsTo: [],
        },
    },

    beforeCreate: encryptPassword,
    beforeUpdate: (values, next) => {
        if (!values.password) {
            delete values.password;

            return next();
        }

        try {
            // check if the password is already hashed
            bcrypt.getRounds(values.password);
        } catch (e) {
            return encryptPassword(values, next);
        }

        next();
    }
};

function encryptPassword(values, next) {
    if (!values.password) {
        return next();
    }

    bcrypt.hash(values.password, 10, (error, hash) => {
        if (error) {
            return next(error);
        }

        values.password = hash;

        next();
    });
};